mapper.select:
SELECT emp_no,dept_no,position_no,password,emp_name,emp_tel,emp_sign,emp_path
FROM employee
where emp_no=#{empNo} and password=#{password}

mapper.update:
update employee set password=#{password}, emp_tel=#{empTel},
   emp_sign=#{empSign}, emp_path=#{empPath}
where emp_no=#{empNo}

update employee set password=#{password}, emp_tel=#{empTel},
   emp_sign=#{empSign}, emp_path=#{empPath}, emp_name=#{empName},
   dept_no=#{departmentVO.deptNo}, position_no=#{positionVO.positionNo}   
where emp_no=#{empNo}
